import React from "react";

const Word = ( {correctLetter, selectedWord} ) => {
    return (
        <div class="word-container">
    {selectedWord.split('')
    .map( (letter,i) => {
    return(
        <span key={i} className="letter">
            {correctLetter.includes(letter) ? letter : ''}
        </span>
    )}
    )}
</div>
    )

}

export default Word
